mapper.select:
SELECT id,member_id AS memberId,num,comment_num as commentNum,fabulous_num as fabulousNum,
 is_delete as isDelete,share_num as shareNum,consulatation_id as consulatation_id,
  collect_num as collectNum,type,model_type as modelType,topic_type as topicType from alq_consultation where id = #{id}

sql.where:
c.member_id=#{id} AND ( c.is_delete=0 OR c.is_delete is null)

	AND c.title like concat('%',#{info},'%')


	AND c.status=#{status}

mapper.select:
SELECT c.id,c.type,c.title,c.status,c.detail_summary descr,c.consulatation_id pid, c.model_type,ifnull(c.collect_num,0) collect_num,c.topic_type,ifnull(c.fabulous_num,0)fabulous_num , ifnull(c.num,0) num, ifnull(c.comment_num,0) comment_num, c.member_id mid,(select m.user_type from alq_member m where m.id=c.member_id) user_type, (select ifnull(m.nickname,INSERT((m.phone),4,4,'****')) from alq_member m where m.id=c.member_id) author_info,
ifnull((select f.type from alq_consultation_fabulous f where f.consultation_id=c.id and f.member_id=#{mid} ),2) flag,
2 flag,
0 follow,
(select count(1) from alq_member_follow w where w.member_id=c.member_id and ifnull(is_delete,0)=0 and w.member_following_id = #{mid} ) follow,
1 collect,
ifnull((select cc.type from alq_collection_consultation cc where cc.consultation_id=c.id and cc.member_id = #{mid}),1) collect,
CASE WHEN DATE_FORMAT(NOW(),'%Y/%m/%d')= DATE_FORMAT(c.created_time,'%Y/%m/%d') THEN DATE_FORMAT(c.created_time,'%H:%i') ELSE DATE_FORMAT(c.created_time,'%Y/%m/%d') END createdTime, (SELECT mt.address FROM alq_member m,alq_attachment mt WHERE m.logo_attachment_id=mt.id AND m.id=c.member_id) photo ,IFNULL(c.fabulous_num,0) as favourNum FROM alq_consultation c,alq_member me where ifnull(c.is_delete,0) = 0 and c.member_id=me.id
AND c.id = #{id}
AND c.status = #{status}
AND c.member_id = #{memberId}
AND c.consulatation_id = #{pid}
AND c.type = #{type}
order by c.created_time desc
limit #{page} ,#{size}

select con.consultation_id as cid, IFNULL( (select cn.consulatation_id from alq_consultation as cn
where cn.id= con.consultation_id and cn.is_delete !=1),null
) as pid from alq_collection_consultation AS con
where type=0 and con.member_id =#{id} order by con.update_time DESC LIMIT #{startIndex},#{endIndex}
