mapper.select:
SELECT * FROM alq_specialist AS spe ORDER BY spe.id ASC
LIMIT 0,6
LIMIT 6,#{specialistCount}

SELECT d.id, d.name, d.sex, d.head_url, concat(d.province_name,d.city_name,d.address) AS doctorAddress, IFNULL(sp.name,"") AS pacageName FROM alq_doctor AS d LEFT JOIN alq_service_package sp ON d.id = sp.doctor_id GROUP BY d.id ORDER BY d.id ASC
LIMIT 0,6
LIMIT 6,#{doctorCount}
