mapper.select:
SELECT COMMEN.content as content, (case when COMMEN.comment_info_id is null then 0 else 1 end) AS info, COMMEN.id as comId, IFNULL(con.type, 0) AS type, mm.nickname AS nickname, mm.id as userId, msg.id as msgId, (case when mm.user_type = 2 then 0 else mm.user_type end )as userType, att.address AS address, DATE_FORMAT(msg.created_time,'%Y/%m/%d %H:%i:%s') AS date , con.title as message, con.id as messageId , IFNULL(msg.read_type,0) as readType , con.type as conType , mm.id as pId , con.consulatation_id as consulatationId , con.id as conId
FROM alq_consultation_comment COMMEN, alq_consultation as con, alq_member AS mm Left JOIN alq_attachment AS att ON att.id = mm.logo_attachment_id, alq_member_msg AS msg WHERE msg.consulatation_comment_id = COMMEN.id AND msg.type=4 AND con.id =#{conId} AND COMMEN.id = #{comId}
AND msg.member_base_id = mm.id AND mm.id = #{memId} AND msg.member_id = #{mLId}

SELECT COMMEN.content, COMMEN.id as comId, (case when COMMEN.comment_info_id is null then 0 else 1 end) AS info, mm.nickname AS nickname, mm.id as userId, msg.id as msgId, (case when mm.user_type = 2 then 0 else mm.user_type end )as userType, att.address AS address, mm.id as pId, IFNULL(msg.read_type,0) as readType , DATE_FORMAT(msg.created_time,'%Y/%m/%d %H:%i:%s') AS date, (CASE WHEN COMMEN.parent_id IS NULL THEN ( SELECT ment.content FROM alq_consultation_comment AS ment WHERE ment.id = COMMEN.comment_info_id )ELSE ( SELECT ment2.content FROM alq_consultation_comment AS ment2 WHERE ment2.id = COMMEN.parent_id )END) message, (CASE WHEN COMMEN.parent_id IS NULL THEN ( SELECT ment.id FROM alq_consultation_comment AS ment WHERE ment.id = COMMEN.comment_info_id )ELSE ( SELECT ment2.id FROM alq_consultation_comment AS ment2 WHERE ment2.id = COMMEN.parent_id )END) messageId , conn.id as conId, conn.type as conType
FROM alq_consultation_comment COMMEN LEFT JOIN alq_consultation as conn on COMMEN.consultation_id = conn.id, alq_member AS mm LEFT JOIN alq_attachment AS att ON att.id = mm.logo_attachment_id, alq_member_msg AS msg WHERE msg.consulatation_comment_id = COMMEN.id AND msg.type=4 AND COMMEN.id = #{comId}
AND msg.member_base_id = mm.id AND mm.id = #{memId} AND msg.member_id = #{mLId}
